CREATE TABLE IF NOT EXISTS "Currencies" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "code" varchar(3) NOT NULL UNIQUE, "name" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "Items" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(255) NOT NULL, "loan_count" integer NOT NULL, "price" decimal NOT NULL, "description" text NULL, "in_stock" bool NOT NULL, "image" varchar(100) NULL);
CREATE TABLE IF NOT EXISTS "Customer" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "account_number" varchar(20) NOT NULL, "id_number" varchar(8) NOT NULL, "county" varchar(100) NULL, "loan_owed" decimal NOT NULL, "loan_limit" decimal NOT NULL, "trust_score" decimal NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "user_id" bigint NOT NULL UNIQUE REFERENCES "Users" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "Payment_Methods" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(30) NOT NULL, "description" text NOT NULL, "icon" varchar(100) NULL, "active" bool NOT NULL, "payment_gateway" varchar(50) NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "Payment_Methods_currency" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "paymentmethod_id" bigint NOT NULL REFERENCES "Payment_Methods" ("id") DEFERRABLE INITIALLY DEFERRED, "currency_id" bigint NOT NULL REFERENCES "Currencies" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "Savings_Items" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "target_amount" decimal NOT NULL, "amount_saved" decimal NOT NULL, "start_date" date NOT NULL, "due_date" date NULL, "achieved" bool NOT NULL, "in_progress" bool NOT NULL, "saving_period" integer NOT NULL, "is_suspended" bool NOT NULL, "item_id" bigint NOT NULL REFERENCES "Items" ("id") DEFERRABLE INITIALLY DEFERRED, "savings_id" bigint NOT NULL REFERENCES "Savings_Accounts" ("id") DEFERRABLE INITIALLY DEFERRED, "customer_id" integer NULL, "payment_ref" varchar(25) NULL);
CREATE TABLE IF NOT EXISTS "Savings_Accounts" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "amount_saved" decimal NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "user_id" bigint NOT NULL UNIQUE REFERENCES "Users" ("id") DEFERRABLE INITIALLY DEFERRED, "customer_id" integer NULL);
CREATE TABLE IF NOT EXISTS "Payments" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "amount" decimal NOT NULL, "type" varchar(50) NOT NULL, "transaction_id" varchar(100) NULL, "payment_ref" varchar(100) NULL, "status" varchar(50) NOT NULL, "payment_date" datetime NOT NULL, "receiving_till" varchar(15) NULL, "receiving_number" varchar(15) NULL, "customer_id" bigint NOT NULL REFERENCES "Customer" ("id") DEFERRABLE INITIALLY DEFERRED, "loan_id" bigint NULL REFERENCES "Loans" ("id") DEFERRABLE INITIALLY DEFERRED, "payment_method_id" bigint NULL REFERENCES "Payment_Methods" ("id") DEFERRABLE INITIALLY DEFERRED, "savings_id" bigint NULL REFERENCES "Savings_Accounts" ("id") DEFERRABLE INITIALLY DEFERRED, "savings_item_id" bigint NULL REFERENCES "Savings_Items" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "Loans" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "amount" decimal NOT NULL, "amount_disbursed" decimal NULL, "deduction_rate" decimal NOT NULL, "loan_duration" integer NOT NULL, "application_date" date NOT NULL, "approval_date" date NULL, "due_date" date NULL, "repaid_amount" decimal NOT NULL, "is_approved" bool NOT NULL, "is_active" bool NOT NULL, "is_disbursed" bool NOT NULL, "default_days" integer NOT NULL, "default_rate" decimal NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "user_id" bigint NOT NULL REFERENCES "Users" ("id") DEFERRABLE INITIALLY DEFERRED, "payment_ref" varchar(25) NULL, "balance" real NOT NULL);
